package com.myapp.climatscope.data.local

import android.content.ContentValues
import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteOpenHelper
import android.util.Log
import com.myapp.climatscope.domain.entities.City

private const val DATABASE_NAME = "climatscope.db"
private const val DATABASE_VERSION = 1

private const val CITY_TABLE_NAME = "city"
private const val CITY_KEY_ID = "id"
private const val CITY_KEY_NAME = "name"

private const val CITY_TABLE_CREATE = """
    CREATE TABLE $CITY_TABLE_NAME (
        $CITY_KEY_ID INTEGER PRIMARY KEY,
        $CITY_KEY_NAME TEXT
)    
"""
private const val CITY_QUERY_SELECT_ALL = "SELECT * FROM $CITY_TABLE_NAME"

class CityLocalDataSource(context: Context) :
    SQLiteOpenHelper(context, DATABASE_NAME, null, DATABASE_VERSION) {

    private val TAG = CityLocalDataSource::class.java.simpleName

    override fun onCreate(db: SQLiteDatabase?) {
        db?.execSQL(CITY_TABLE_CREATE)
    }

    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {
        // Handle database upgrade if needed
    }

    suspend fun createCity(city: City): Boolean {
        return try {
            val values = ContentValues().apply {
                put(CITY_KEY_NAME, city.name)
            }

            Log.d(TAG, "creating City: $values")
            val id = writableDatabase.insert(CITY_TABLE_NAME, null, values)
            id > 0
        } catch (e: Exception) {
            Log.e(TAG, "Error creating city", e)
            false
        }
    }

    suspend fun getAllCities(): List<City> {
        val cities = mutableListOf<City>()
        try {
            readableDatabase.rawQuery(CITY_QUERY_SELECT_ALL, null).use { cursor ->
                while (cursor.moveToNext()) {
                    val city = City(
                        id = cursor.getLong(cursor.getColumnIndexOrThrow(CITY_KEY_ID)),
                        name = cursor.getString(cursor.getColumnIndexOrThrow(CITY_KEY_NAME))
                    )
                    cities.add(city)
                }
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error getting cities", e)
        }
        return cities
    }

    suspend fun deleteCity(city: City): Boolean {
        return try {
            Log.d(TAG, "delete city: $city")
            val deleteCount = writableDatabase.delete(
                CITY_TABLE_NAME,
                "$CITY_KEY_ID = ?",
                arrayOf(city.id.toString())
            )
            deleteCount == 1
        } catch (e: Exception) {
            Log.e(TAG, "Error deleting city", e)
            false
        }
    }
}

package com.myapp.climatscope.data.remote

import com.myapp.climatscope.BuildConfig
import retrofit2.Response
import retrofit2.http.GET
import retrofit2.http.Query

interface WeatherApiService {
    @GET("data/2.5/weather?units=metric&lang=fr")
    suspend fun getWeather(
        @Query("q") cityName: String,
        @Query("appid") apiKey: String = BuildConfig.OPENWEATHER_API_KEY,
    ): Response<WeatherResponse>

    @GET("data/2.5/weather?units=metric&lang=fr")
    suspend fun getWeatherByCoordinates(
        @Query("lat") latitude: Double,
        @Query("lon") longitude: Double,
        @Query("appid") apiKey: String = BuildConfig.OPENWEATHER_API_KEY,
        @Query("lang") language: String = "en"
}

package com.myapp.climatscope.data.remote

import com.myapp.climatscope.BuildConfig
import retrofit2.Response
import retrofit2.http.GET
import retrofit2.http.Query

interface WeatherApiService {
    @GET("data/2.5/weather?units=metric")
    suspend fun getWeather(
        @Query("q") cityName: String,
        @Query("appid") apiKey: String = BuildConfig.OPENWEATHER_API_KEY,
        @Query("lang") language: String = "en"
    ): Response<WeatherResponse>

    @GET("data/2.5/weather?units=metric")
    suspend fun getWeatherByCoordinates(
        @Query("lat") latitude: Double,
        @Query("lon") longitude: Double,
        @Query("appid") apiKey: String = BuildConfig.OPENWEATHER_API_KEY,
        @Query("lang") language: String = "en"
    ): Response<WeatherResponse>

    // API pour rechercher des villes dans le monde entier
    @GET("geo/1.0/direct")
    suspend fun searchCities(
        @Query("q") query: String,
        @Query("limit") limit: Int = 5,
        @Query("appid") apiKey: String = BuildConfig.OPENWEATHER_API_KEY
    ): Response<List<GeocodingResponse>>

    // API pour obtenir le nom de la ville par coordonnées (géolocalisation inverse)
    @GET("geo/1.0/reverse")
    suspend fun getCityNameByCoordinates(
        @Query("lat") latitude: Double,
        @Query("lon") longitude: Double,
        @Query("limit") limit: Int = 1,
        @Query("appid") apiKey: String = BuildConfig.OPENWEATHER_API_KEY
    ): Response<List<GeocodingResponse>>
}

package com.myapp.climatscope.data.remote

import android.util.Log
import com.myapp.climatscope.domain.entities.Weather
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

class WeatherRemoteDataSource(
    private val apiService: WeatherApiService
) {
    suspend fun getWeatherForCity(cityName: String): Result<Weather> {
        return withContext(Dispatchers.IO) {
            try {
                val response = apiService.getWeather("$cityName,fr")
                if (response.isSuccessful) {
                    response.body()?.let { weatherResponse ->
                        val weather = mapToWeather(weatherResponse)
                        Result.success(weather)
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Result.failure(Exception("API Error: ${response.code()}"))
                }
            } catch (e: Exception) {
                Result.failure(e)
            }
        }
    }

    suspend fun getWeatherByCoordinates(latitude: Double, longitude: Double): Result<Weather> {
        return withContext(Dispatchers.IO) {
            try {
                val response = apiService.getWeatherByCoordinates(latitude, longitude)
                if (response.isSuccessful) {
                    response.body()?.let { weatherResponse ->
                        val weather = mapToWeather(weatherResponse)
                        Result.success(weather)
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Result.failure(Exception("API Error: ${response.code()}"))
                }
            } catch (e: Exception) {
                Result.failure(e)
            }
        }
    }

    private fun mapToWeather(response: WeatherResponse): Weather {
        val weatherFirst = response.weather.first()
        return Weather(
            description = weatherFirst.description,
            temperature = response.main.temperature,
            feelsLike = response.main.feelsLike,
            humidity = response.main.humidity,
            pressure = response.main.pressure,
            windSpeed = response.wind?.speed ?: 0.0,
            iconUrl = "https://openweathermap.org/img/wn/${weatherFirst.icon}.png"
        )
    }
}

package com.myapp.climatscope.data.remote

import android.util.Log
import com.myapp.climatscope.domain.entities.Weather
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

class WeatherRemoteDataSource(
    private val apiService: WeatherApiService
) {
    suspend fun getWeatherForCity(cityName: String): Result<Weather> {
        return withContext(Dispatchers.IO) {
            try {
                // Suppression de ",fr" pour permettre la recherche mondiale
                val response = apiService.getWeather(cityName)
                if (response.isSuccessful) {
                    response.body()?.let { weatherResponse ->
                        val weather = mapToWeather(weatherResponse)
                        Result.success(weather)
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Log.e("WeatherAPI", "API Error: ${response.code()} - ${response.message()}")
                    Result.failure(Exception("API Error: ${response.code()} - ${response.message()}"))
                }
            } catch (e: Exception) {
                Log.e("WeatherAPI", "Exception: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    suspend fun getWeatherByCoordinates(latitude: Double, longitude: Double): Result<Weather> {
        return withContext(Dispatchers.IO) {
            try {
                val response = apiService.getWeatherByCoordinates(latitude, longitude)
                if (response.isSuccessful) {
                    response.body()?.let { weatherResponse ->
                        val weather = mapToWeather(weatherResponse)
                        Result.success(weather)
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Log.e("WeatherAPI", "API Error: ${response.code()} - ${response.message()}")
                    Result.failure(Exception("API Error: ${response.code()} - ${response.message()}"))
                }
            } catch (e: Exception) {
                Log.e("WeatherAPI", "Exception: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    // Nouvelle fonction pour rechercher des villes dans le monde entier
    suspend fun searchCities(query: String): Result<List<GeocodingResponse>> {
        return withContext(Dispatchers.IO) {
            try {
                val response = apiService.searchCities(query)
                if (response.isSuccessful) {
                    response.body()?.let { cities ->
                        Result.success(cities)
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Log.e("WeatherAPI", "Search Cities Error: ${response.code()} - ${response.message()}")
                    Result.failure(Exception("API Error: ${response.code()} - ${response.message()}"))
                }
            } catch (e: Exception) {
                Log.e("WeatherAPI", "Search Cities Exception: ${e.message}", e)
                Result.failure(e)
            }
        }
    }

    // Nouvelle fonction pour obtenir le nom de la ville par coordonnées
    suspend fun getCityNameByCoordinates(latitude: Double, longitude: Double): Result<String> {
        return withContext(Dispatchers.IO) {
            try {
                val response = apiService.getCityNameByCoordinates(latitude, longitude)
                if (response.isSuccessful) {
                    response.body()?.let { cities ->
                        if (cities.isNotEmpty()) {
                            val city = cities.first()
                            val cityName = if (city.state != null && city.country.isNotEmpty()) {
                                "${city.name}, ${city.state}, ${city.country}"
                            } else {
                                "${city.name}, ${city.country}"
                            }
                            Result.success(cityName)
                        } else {
                            Result.success("Position actuelle")
                        }
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Log.e("WeatherAPI", "Reverse Geocoding Error: ${response.code()} - ${response.message()}")
                    Result.success("Ma position") // Fallback en cas d'erreur
                }
            } catch (e: Exception) {
                Log.e("WeatherAPI", "Reverse Geocoding Exception: ${e.message}", e)
                Result.success("Ma position") // Fallback en cas d'erreur
            }
        }
    }

    private fun mapToWeather(response: WeatherResponse): Weather {
        val weatherFirst = response.weather.first()
        return Weather(
            description = weatherFirst.description,
            temperature = response.main.temperature,
            feelsLike = response.main.feelsLike,
            humidity = response.main.humidity,
            pressure = response.main.pressure,
            windSpeed = response.wind?.speed ?: 0.0,
            iconUrl = "https://openweathermap.org/img/wn/${weatherFirst.icon}.png"
        )
    }
}

