                weather = Weather(
                    temperature = 35.0,
                    description = "Ensoleillé",
                    humidity = 45,
                    pressure = 1013,
                    windSpeed = 10.0,
                    feelsLike = 38.0,
                    iconUrl = ""
                )
            )
        }
    }
}

