@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun SmartCitySearchBar(
    onCitySelected: (GeocodingResponse) -> Unit,
    onDismiss: () -> Unit,
    searchViewModel: CitySearchViewModel,
    modifier: Modifier = Modifier
) {
    var searchQuery by remember { mutableStateOf("") }
    var isExpanded by remember { mutableStateOf(false) }
    val focusRequester = remember { FocusRequester() }
    val keyboardController = LocalSoftwareKeyboardController.current

    val searchState by searchViewModel.searchState.collectAsState()

    // Déclencher la recherche avec un délai pour éviter trop d'appels API
    LaunchedEffect(searchQuery) {
        if (searchQuery.length >= 2) {
            delay(300) // Délai de 300ms
            searchViewModel.searchCities(searchQuery)
        } else {
            searchViewModel.clearResults()
        }
    }

    // Auto-focus au premier affichage
    LaunchedEffect(Unit) {
        focusRequester.requestFocus()
    }

    Card(
        modifier = modifier
            .fillMaxWidth()
            .padding(16.dp),
        elevation = CardDefaults.cardElevation(defaultElevation = 8.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface
        )
    ) {
        Column(
            modifier = Modifier.padding(16.dp)
        ) {
            // Barre de recherche
            SearchTextField(
                query = searchQuery,
                onQueryChange = {
                    searchQuery = it
                    isExpanded = it.isNotEmpty()
                },
                onClear = {
                    searchQuery = ""
                    isExpanded = false
                    searchViewModel.clearResults()
                },
                focusRequester = focusRequester,
                modifier = Modifier.fillMaxWidth()
            )

            Spacer(modifier = Modifier.height(8.dp))

            // Boutons d'action
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.End
            ) {
                TextButton(onClick = onDismiss) {
                    Text("Annuler")
                }
            }

            // Résultats de recherche
            AnimatedVisibility(
                visible = isExpanded,
                enter = expandVertically() + fadeIn(),
                exit = shrinkVertically() + fadeOut()
            ) {
                SearchResultsContent(
                    searchState = searchState,
                    searchQuery = searchQuery,
                    onCitySelected = { city ->
                        onCitySelected(city)
                        onDismiss()
                    }
                )
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun SearchTextField(
    query: String,
    onQueryChange: (String) -> Unit,
    onClear: () -> Unit,
    focusRequester: FocusRequester,
    modifier: Modifier = Modifier
) {
    OutlinedTextField(
        value = query,
        onValueChange = onQueryChange,
        label = { Text("Rechercher une ville...") },
        leadingIcon = {
            Icon(
                imageVector = Icons.Default.Search,
                contentDescription = "Rechercher"
            )
        },
        trailingIcon = {
            if (query.isNotEmpty()) {
                IconButton(onClick = onClear) {
                    Icon(
                        imageVector = Icons.Default.Clear,
                        contentDescription = "Effacer"
                    )
                }
            }
        },
        singleLine = true,
        modifier = modifier.focusRequester(focusRequester),
        colors = OutlinedTextFieldDefaults.colors(
            focusedBorderColor = MaterialTheme.colorScheme.primary,
            focusedLabelColor = MaterialTheme.colorScheme.primary
        )
    )
}

@Composable
private fun SearchResultsContent(
    searchState: CitySearchViewModel.SearchState,
    searchQuery: String,
    onCitySelected: (GeocodingResponse) -> Unit
) {
    Column(
        modifier = Modifier
            .fillMaxWidth()
            .heightIn(max = 300.dp)
    ) {
        when {
            searchState.isLoading -> {
                LoadingSearchResults()
            }

            searchState.error != null -> {
                ErrorSearchResults(error = searchState.error)
            }

            searchState.cities.isEmpty() && searchQuery.length >= 2 -> {
                NoResultsFound(searchQuery = searchQuery)
            }

            searchState.cities.isNotEmpty() -> {
                CitySearchResults(
                    cities = searchState.cities,
                    onCitySelected = onCitySelected
                )
            }

            else -> {
                SearchInstructions()
            }
        }
    }
}

