import com.myapp.climatscope.presentation.viewmodels.WeatherViewModel

sealed class Screen(val route: String) {
    object LocationPermission : Screen("location_permission")
    object Home : Screen("home")
    object CitySelection : Screen("city_selection")
    object Weather : Screen("weather/{cityName}") {
        fun createRoute(cityName: String) = "weather/$cityName"
    }
}

@Composable
fun ClimatScopeNavigation(
    navController: NavHostController = rememberNavController(),
    app: ClimatScopeApplication
) {
    NavHost(
        navController = navController,
        startDestination = Screen.Home.route // Démarrage direct sur l'écran principal
    ) {
        // Écran de demande de localisation (optionnel)
        composable(Screen.LocationPermission.route) {
            LocationPermissionScreen(
                onLocationGranted = {
                    navController.navigate(Screen.Home.route) {
                        popUpTo(Screen.LocationPermission.route) { inclusive = true }
                    }
                },
                onLocationDenied = {
                    navController.navigate(Screen.Home.route) {
                        popUpTo(Screen.LocationPermission.route) { inclusive = true }
                    }
                }
            )
        }

        // Écran principal simplifié
        composable(Screen.Home.route) {
            val weatherViewModel: WeatherViewModel = viewModel(
                factory = app.dependencyContainer.getWeatherViewModelFactory()
            )
            val cityViewModel: CityViewModel = viewModel(
                factory = app.dependencyContainer.getCityViewModelFactory()
            )

            HomeScreen(
                weatherViewModel = weatherViewModel,
                cityViewModel = cityViewModel
            )
        }

        // Écran de sélection des villes (optionnel)
        composable(Screen.CitySelection.route) {
            val cityViewModel: CityViewModel = viewModel(
                factory = app.dependencyContainer.getCityViewModelFactory()
            )
            val weatherViewModel: WeatherViewModel = viewModel(
                factory = app.dependencyContainer.getWeatherViewModelFactory()
            )

            CitySelectionScreen(
                cityViewModel = cityViewModel,
                onCitySelected = { city ->
                    weatherViewModel.loadWeatherForCity(city.name)
                    navController.navigate(Screen.Home.route) {
                        popUpTo(Screen.CitySelection.route) { inclusive = true }
                    }
                },
                onBackPressed = {
                    navController.popBackStack()
                }
            )
        }

        // Écran détaillé d'une ville (optionnel)
        composable(Screen.Weather.route) { backStackEntry ->
            val cityName = backStackEntry.arguments?.getString("cityName") ?: ""
            val weatherViewModel: WeatherViewModel = viewModel(
                factory = app.dependencyContainer.getWeatherViewModelFactory()
            )

            WeatherScreen(
                cityName = cityName,
                onBackClick = {
                    navController.popBackStack()
                },
                viewModel = weatherViewModel
            )
        }
    }
}

package com.myapp.climatscope.presentation.screens

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.style.TextAlign
import androidx.compose.ui.tooling.preview.Preview
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.myapp.climatscope.domain.entities.City
import com.myapp.climatscope.presentation.theme.ClimatScopeTheme
import com.myapp.climatscope.presentation.viewmodels.CityViewModel

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun CitySelectionScreen(
    cityViewModel: CityViewModel = viewModel(),
    onCitySelected: (City) -> Unit,
    onBackPressed: () -> Unit = {}
) {
    val cityUiState by cityViewModel.uiState.collectAsState()
    var showAddDialog by remember { mutableStateOf(false) }

    Column(
        modifier = Modifier
            .fillMaxSize()
            .background(MaterialTheme.colorScheme.background)
            .statusBarsPadding()
    ) {
        // Header
        TopAppBar(
            title = {
                Text(
                    text = "Choisir une ville",
                    fontSize = 20.sp,
                    fontWeight = FontWeight.Medium
                )
            },
            navigationIcon = {
                IconButton(onClick = onBackPressed) {
                    Icon(
                        imageVector = Icons.Default.ArrowBack,
                        contentDescription = "Retour"
                    )
                }
            },
            actions = {
                IconButton(onClick = { showAddDialog = true }) {
                    Icon(
                        imageVector = Icons.Default.Add,
                        contentDescription = "Ajouter ville"
                    )
                }
            },
            colors = TopAppBarDefaults.topAppBarColors(
                containerColor = MaterialTheme.colorScheme.background
            )
        )

        when {
            cityUiState.isLoading -> {
                Box(
                    modifier = Modifier.fillMaxSize(),
                    contentAlignment = Alignment.Center
                ) {
                    CircularProgressIndicator(
                        color = MaterialTheme.colorScheme.primary
                    )
                }
            }
            cityUiState.cities.isEmpty() -> {
                EmptyStateContent(
                    onAddCityClick = { showAddDialog = true }
                )
            }
            else -> {
                LazyColumn(
                    modifier = Modifier.fillMaxSize(),
                    contentPadding = PaddingValues(16.dp),
                    verticalArrangement = Arrangement.spacedBy(12.dp)
                ) {
                    items(cityUiState.cities) { city ->
                        CityCard(
                            city = city,
                            onCityClick = { onCitySelected(city) },
                            onDeleteCity = { cityViewModel.deleteCity(city) }
                        )
                    }
                }
            }
        }
    }

    if (showAddDialog) {
        AddCityDialog(
            onAddCity = { cityName ->
                cityViewModel.createCity(cityName)
                showAddDialog = false
            },
            onDismiss = { showAddDialog = false }
        )
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun CityCard(
    city: City,
    onCityClick: () -> Unit,
    onDeleteCity: () -> Unit
) {
    Card(
        onClick = onCityClick,
        modifier = Modifier.fillMaxWidth(),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surfaceContainer
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 2.dp)
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(20.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Row(
                verticalAlignment = Alignment.CenterVertically,
                horizontalArrangement = Arrangement.spacedBy(16.dp)
            ) {
                Box(
                    modifier = Modifier
                        .size(48.dp)
                        .clip(CircleShape)
                        .background(MaterialTheme.colorScheme.primary.copy(alpha = 0.1f)),
                    contentAlignment = Alignment.Center
                ) {
                    Text(
                        text = city.name.first().uppercase(),
                        fontSize = 20.sp,
                        fontWeight = FontWeight.Bold,
                        color = MaterialTheme.colorScheme.primary
                    )
                }

                Column {
                    Text(
                        text = city.name,
                        fontSize = 18.sp,
                        fontWeight = FontWeight.Medium,
                        color = MaterialTheme.colorScheme.onSurface
                    )
                    Text(
                        text = "Appuyez pour sélectionner",
                        fontSize = 14.sp,
                        color = MaterialTheme.colorScheme.onSurfaceVariant
                    )
                }
            }

            IconButton(
                onClick = onDeleteCity,
                colors = IconButtonDefaults.iconButtonColors(
                    contentColor = MaterialTheme.colorScheme.error
                )
            ) {
                Icon(
                    Icons.Default.Delete,
                    contentDescription = "Supprimer ${city.name}",
                    modifier = Modifier.size(20.dp)
                )
            }
        }
    }
}

@Composable
private fun EmptyStateContent(
    onAddCityClick: () -> Unit
) {
    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(32.dp),
        horizontalAlignment = Alignment.CenterHorizontally,
        verticalArrangement = Arrangement.Center
    ) {
        Icon(
            imageVector = Icons.Default.LocationOn,
            contentDescription = null,
            modifier = Modifier.size(64.dp),
            tint = MaterialTheme.colorScheme.onSurfaceVariant
        )

        Spacer(modifier = Modifier.height(24.dp))

        Text(
            text = "Aucune ville ajoutée",
            fontSize = 24.sp,
            fontWeight = FontWeight.Bold,
            textAlign = TextAlign.Center,
            color = MaterialTheme.colorScheme.onSurface
        )

        Spacer(modifier = Modifier.height(8.dp))

        Text(
            text = "Commencez par ajouter votre première ville pour suivre la météo",
            fontSize = 16.sp,
            color = MaterialTheme.colorScheme.onSurfaceVariant,
            textAlign = TextAlign.Center
        )

        Spacer(modifier = Modifier.height(32.dp))

        Button(
            onClick = onAddCityClick,
            modifier = Modifier
                .fillMaxWidth()
                .height(56.dp),
            shape = RoundedCornerShape(16.dp)
        ) {
            Icon(Icons.Default.Add, contentDescription = null)
            Spacer(modifier = Modifier.width(8.dp))
            Text(
                text = "Ajouter une ville",
                fontSize = 16.sp,
                fontWeight = FontWeight.Medium
            )
        }
    }
}

@Composable
private fun AddCityDialog(
    onAddCity: (String) -> Unit,
    onDismiss: () -> Unit
) {
    var cityName by remember { mutableStateOf("") }
    var isError by remember { mutableStateOf(false) }

    AlertDialog(
        onDismissRequest = onDismiss,
        title = {
            Text(
                text = "Ajouter une ville",
                fontSize = 20.sp,
                fontWeight = FontWeight.Bold
            )
        },
        text = {
            Column(verticalArrangement = Arrangement.spacedBy(8.dp)) {
                Text(
                    text = "Entrez le nom de la ville que vous souhaitez ajouter",
                    fontSize = 14.sp,
                    color = MaterialTheme.colorScheme.onSurfaceVariant
                )

                OutlinedTextField(
                    value = cityName,
                    onValueChange = {
                        cityName = it
                        isError = false
                    },
                    label = { Text("Nom de la ville") },
                    placeholder = { Text("ex: Paris, Londres...") },
                    singleLine = true,
                    isError = isError,
                    supportingText = if (isError) {
                        { Text("Veuillez entrer un nom de ville valide") }
                    } else null,
                    modifier = Modifier.fillMaxWidth()
                )
            }
        },
        confirmButton = {
            Button(
                onClick = {
                    if (cityName.isBlank()) {
                        isError = true
                    } else {
                        onAddCity(cityName.trim())
                    }
                },
                enabled = cityName.isNotBlank()
            ) {
                Text("Ajouter")
            }
        },
        dismissButton = {
            TextButton(onClick = onDismiss) {
                Text("Annuler")
            }
        }
    )
}

@Preview(showBackground = true)
@Composable
fun CitySelectionScreenPreview() {
    ClimatScopeTheme {
        CitySelectionScreen(
            onCitySelected = {},
            onBackPressed = {}
        )
    }
}

