import com.myapp.climatscope.presentation.viewmodels.CityViewModel
import com.myapp.climatscope.presentation.viewmodels.WeatherViewModel
import kotlinx.coroutines.launch

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun ModernHomeScreen(
    onCityClick: (City) -> Unit,
    cityViewModel: CityViewModel = viewModel(),
    weatherViewModel: WeatherViewModel = viewModel()
) {
    val cityUiState by cityViewModel.uiState.collectAsState()
    val weatherUiState by weatherViewModel.uiState.collectAsState()
    var showBottomSheet by remember { mutableStateOf(false) }
    val context = LocalContext.current
    val app = context.applicationContext as ClimatScopeApplication
    val scope = rememberCoroutineScope()

    // Gestionnaire de permissions de géolocalisation moderne
    val locationPermissionLauncher = rememberLauncherForActivityResult(
        contract = ActivityResultContracts.RequestMultiplePermissions()
    ) { permissions ->
        val fineLocationGranted = permissions[Manifest.permission.ACCESS_FINE_LOCATION] ?: false
        val coarseLocationGranted = permissions[Manifest.permission.ACCESS_COARSE_LOCATION] ?: false

        if (fineLocationGranted || coarseLocationGranted) {
            scope.launch {
                try {
                    val result = app.dependencyContainer.getWeatherByLocationUseCase().invoke()
                    result.fold(
                        onSuccess = { (weather, location) ->
                            weatherViewModel.setLocationWeather(weather, "Ma position (${location.latitude.toInt()}, ${location.longitude.toInt()})")
                        },
                        onFailure = { error ->
                            println("Erreur de géolocalisation: ${error.message}")
                            weatherViewModel.loadWeatherForCity("Paris") // Ville par défaut
                        }
                    )
                } catch (e: Exception) {
                    println("Erreur lors de la récupération de la localisation: ${e.message}")
                    weatherViewModel.loadWeatherForCity("Paris")
                }
            }
        }
    }

    // Chargement initial des données
    LaunchedEffect(Unit) {
        cityViewModel.loadCities()

        // Charger une ville par défaut
        if (cityUiState.cities.isNotEmpty()) {
            val firstCity = cityUiState.cities.first()
            weatherViewModel.loadWeatherForCity(firstCity.name)
        } else {
            weatherViewModel.loadWeatherForCity("Paris")
        }
    }

    Column(
        modifier = Modifier
            .fillMaxSize()
            .background(
                Brush.verticalGradient(
                    colors = listOf(
                        MaterialTheme.colorScheme.primary.copy(alpha = 0.1f),
                        MaterialTheme.colorScheme.background
                    )
                )
            )
    ) {
        // Top Bar moderne avec géolocalisation
        ModernTopBar(
            onLocationClick = {
                locationPermissionLauncher.launch(
                    arrayOf(
                        Manifest.permission.ACCESS_FINE_LOCATION,
                        Manifest.permission.ACCESS_COARSE_LOCATION
                    )
                )
            },
            onCitiesClick = { showBottomSheet = true }
        )

        // Contenu principal
        LazyColumn(
            modifier = Modifier.fillMaxSize(),
            contentPadding = PaddingValues(16.dp),
            verticalArrangement = Arrangement.spacedBy(20.dp)
        ) {
            // Indicateur de chargement ou carte météo principale
            item {
                val currentWeather = weatherUiState.weather
                val currentError = weatherUiState.errorMessage

                if (weatherUiState.isLoading) {
                    // Afficher un indicateur de chargement
                    Column(
                        modifier = Modifier
                            .fillMaxWidth()
                            .padding(48.dp),
                        horizontalAlignment = Alignment.CenterHorizontally
                    ) {
                        CircularProgressIndicator(
                            modifier = Modifier.size(48.dp),
                            color = MaterialTheme.colorScheme.primary
                        )
                        Spacer(modifier = Modifier.height(16.dp))
                        Text(
                            text = "Chargement de la météo...",
                            style = MaterialTheme.typography.bodyLarge,
                            color = MaterialTheme.colorScheme.onSurfaceVariant
                        )
                    }
                } else if (currentError != null) {
                    // Afficher une carte d'erreur avec possibilité de retry
                    Card(
                        modifier = Modifier.fillMaxWidth(),
                        shape = RoundedCornerShape(24.dp),
                        colors = CardDefaults.cardColors(
                            containerColor = MaterialTheme.colorScheme.errorContainer
                        )
                    ) {
                        Column(
                            modifier = Modifier
                                .fillMaxWidth()
                                .padding(24.dp),
                            horizontalAlignment = Alignment.CenterHorizontally
                        ) {
                            Icon(
                                imageVector = Icons.Default.Error,
                                contentDescription = null,
                                tint = MaterialTheme.colorScheme.error,
                                modifier = Modifier.size(48.dp)
                            )
                            Spacer(modifier = Modifier.height(16.dp))
                            Text(
                                text = "Erreur de chargement",
                                style = MaterialTheme.typography.titleMedium,
                                fontWeight = FontWeight.Bold,
                                color = MaterialTheme.colorScheme.onErrorContainer
                            )
                            Text(
                                text = weatherUiState.errorMessage ?: "Erreur inconnue",
                                style = MaterialTheme.typography.bodyMedium,
                                color = MaterialTheme.colorScheme.onErrorContainer,
                                textAlign = TextAlign.Center
                            )
                            Spacer(modifier = Modifier.height(16.dp))
                            Button(
                                onClick = {
                                    weatherViewModel.loadWeatherForCity("Paris")
                                },
                                colors = ButtonDefaults.buttonColors(
                                    containerColor = MaterialTheme.colorScheme.error
                                )
                            ) {
                                Icon(Icons.Default.Refresh, contentDescription = null)
                                Spacer(modifier = Modifier.width(8.dp))
                                Text("Réessayer")
                            }
                        }
                    }
                } else if (weatherUiState.weather != null) {
                    // Afficher la carte météo normale si on a des données
                    weatherUiState.weather?.let { weather ->
                        ModernWeatherCard(
                            weather = weather,
                            cityName = weatherUiState.cityName.ifEmpty { "Ma position" },
                            onRefresh = {
                                if (weatherUiState.cityName.isNotEmpty()) {
                                    weatherViewModel.loadWeatherForCity(weatherUiState.cityName)
                                }
                            }
                        )
                    }
                }
            }

            // Indicateurs météo détaillés - seulement si on a des données
            weatherUiState.weather?.let { currentWeather ->
                if (!weatherUiState.isLoading) {
                    item {
                        ModernWeatherIndicators(weather = currentWeather)
                    }
                }
            }

            // Carte de conseils météo - seulement si on a des données
            weatherUiState.weather?.let { currentWeather ->
                if (!weatherUiState.isLoading) {
                    item {
                        WeatherAdviceCard(weather = currentWeather)
                    }
                }
            }

            // Liste des villes favorites
            item {
                ModernCitiesSection(
                    cities = cityUiState.cities,
                    onCityClick = onCityClick,
                    onAddCityClick = { showBottomSheet = true }
                )
            }
        }
    }

    // Bottom Sheet pour la gestion des villes
    if (showBottomSheet) {
        EnhancedCityBottomSheet(
            cities = cityUiState.cities,
            isLoading = cityUiState.isLoading,
            onCitySelected = { city ->
                onCityClick(city)
                weatherViewModel.loadWeatherForCity(city.name)
                showBottomSheet = false
            },
            onAddCity = { cityName ->
                cityViewModel.createCity(cityName)
            },
            onDeleteCity = { city ->
                cityViewModel.deleteCity(city)
            },
            onDismiss = { showBottomSheet = false }
        )
    }

    // Gestion des états d'erreur
    weatherUiState.errorMessage?.let { error ->
        LaunchedEffect(error) {
            // Afficher un snackbar d'erreur ou gérer l'erreur
            println("Erreur météo: $error")
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun ModernTopBar(
    onLocationClick: () -> Unit,
    onCitiesClick: () -> Unit,
    modifier: Modifier = Modifier
) {
    TopAppBar(
        title = {
            Text(
                text = "ClimatScope",
                style = MaterialTheme.typography.headlineMedium,
                fontWeight = FontWeight.Bold
            )
        },
        actions = {
            IconButton(onClick = onLocationClick) {
                Icon(
                    imageVector = Icons.Default.MyLocation,
                    contentDescription = "Ma position",
                    tint = MaterialTheme.colorScheme.primary
                )
            }
            IconButton(onClick = onCitiesClick) {
                Icon(
                    imageVector = Icons.Default.List,
                    contentDescription = "Mes villes"
                )
            }
        },
        colors = TopAppBarDefaults.topAppBarColors(
            containerColor = Color.Transparent
        ),
        modifier = modifier
    )
}

@Composable
private fun ModernWeatherCard(
    weather: Weather,
    cityName: String,
    onRefresh: () -> Unit,
    modifier: Modifier = Modifier
) {
    Card(
        modifier = modifier.fillMaxWidth(),
        shape = RoundedCornerShape(24.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surfaceContainer
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 8.dp)
    ) {
        Column(
            modifier = Modifier.padding(24.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Column {
                    Text(
                        text = cityName,
                        style = MaterialTheme.typography.headlineSmall,
                        fontWeight = FontWeight.Bold
                    )
                    Text(
                        text = weather.description,
                        style = MaterialTheme.typography.bodyLarge,
                        color = MaterialTheme.colorScheme.onSurfaceVariant
                    )
                }

                IconButton(onClick = onRefresh) {
                    Icon(
                        imageVector = Icons.Default.Refresh,
                        contentDescription = "Actualiser"
                    )
                }
            }

            Spacer(modifier = Modifier.height(24.dp))

            // Température principale
            Text(
                text = "${weather.temperature.toInt()}°",
                style = MaterialTheme.typography.displayLarge,
                fontWeight = FontWeight.Light,
                color = MaterialTheme.colorScheme.primary
            )

            Text(
                text = "Ressenti ${weather.feelsLike.toInt()}°",
                style = MaterialTheme.typography.bodyMedium,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )
        }
    }
}

@Composable
private fun ModernWeatherIndicators(
    weather: Weather,
    modifier: Modifier = Modifier
) {
    Row(
        modifier = modifier.fillMaxWidth(),
        horizontalArrangement = Arrangement.spacedBy(12.dp)
    ) {
        WeatherIndicatorCard(
            title = "Humidité",
            value = "${weather.humidity}%",
            icon = Icons.Default.Opacity, // Remplace WaterDrop par Opacity
            modifier = Modifier.weight(1f)
        )

        WeatherIndicatorCard(
            title = "Pression",
            value = "${weather.pressure} hPa",
            icon = Icons.Default.BarChart, // Remplace Speed par BarChart
            modifier = Modifier.weight(1f)
        )

        WeatherIndicatorCard(
            title = "Vent",
            value = "${weather.windSpeed.toInt()} km/h",
            icon = Icons.Default.Air,
            modifier = Modifier.weight(1f)
        )
    }
}

@Composable
private fun WeatherIndicatorCard(
    title: String,
    value: String,
    icon: ImageVector,
    modifier: Modifier = Modifier
) {
    Card(
        modifier = modifier,
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface
        )
    ) {
        Column(
            modifier = Modifier.padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Icon(
                imageVector = icon,
                contentDescription = title,
                tint = MaterialTheme.colorScheme.primary,
                modifier = Modifier.size(24.dp)
            )

            Spacer(modifier = Modifier.height(8.dp))

            Text(
                text = value,
                style = MaterialTheme.typography.titleMedium,
                fontWeight = FontWeight.Bold
            )

            Text(
                text = title,
                style = MaterialTheme.typography.bodySmall,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )
        }
    }
}

@Composable
private fun ModernCitiesSection(
    cities: List<City>,
    onCityClick: (City) -> Unit,
    onAddCityClick: () -> Unit,
    modifier: Modifier = Modifier
) {
    Column(modifier = modifier) {
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Mes villes",
                style = MaterialTheme.typography.titleLarge,
                fontWeight = FontWeight.Bold
            )

            TextButton(onClick = onAddCityClick) {
                Icon(Icons.Default.Add, contentDescription = null)
                Spacer(modifier = Modifier.width(4.dp))
                Text("Ajouter")
            }
        }

        Spacer(modifier = Modifier.height(12.dp))

        if (cities.isEmpty()) {
            Card(
                modifier = Modifier.fillMaxWidth(),
                shape = RoundedCornerShape(16.dp),
                colors = CardDefaults.cardColors(
                    containerColor = MaterialTheme.colorScheme.surfaceVariant.copy(alpha = 0.5f)
                )
            ) {
                Column(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(24.dp),
                    horizontalAlignment = Alignment.CenterHorizontally
                ) {
                    Icon(
                        imageVector = Icons.Default.Cloud,
                        contentDescription = null,
                        modifier = Modifier.size(48.dp),
                        tint = MaterialTheme.colorScheme.onSurfaceVariant
                    )

                    Spacer(modifier = Modifier.height(12.dp))

                    Text(
                        text = "Aucune ville ajoutée",
                        style = MaterialTheme.typography.titleMedium,
                        fontWeight = FontWeight.Medium,
                        textAlign = TextAlign.Center
                    )

                    Text(
                        text = "Ajoutez vos villes favorites pour suivre leur météo",
                        style = MaterialTheme.typography.bodyMedium,
                        color = MaterialTheme.colorScheme.onSurfaceVariant,
                        textAlign = TextAlign.Center
                    )
                }
            }
        } else {
            LazyRow(
                horizontalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                items(cities) { city ->
                    CityQuickCard(
                        city = city,
                        onClick = { onCityClick(city) }
                    )
                }
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun CityQuickCard(
    city: City,
    onClick: () -> Unit,
    modifier: Modifier = Modifier
) {
    Card(
        onClick = onClick,
        modifier = modifier.width(120.dp),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface
        )
    ) {
        Column(
            modifier = Modifier.padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Icon(
                imageVector = Icons.Default.LocationCity,
                contentDescription = null,
                tint = MaterialTheme.colorScheme.primary,
                modifier = Modifier.size(24.dp)
            )

            Spacer(modifier = Modifier.height(8.dp))

            Text(
                text = city.name,
                style = MaterialTheme.typography.bodyMedium,
                fontWeight = FontWeight.Medium,
                textAlign = TextAlign.Center,
                maxLines = 2
            )
        }
    }
}

@Preview(showBackground = true)
@Composable
fun ModernHomeScreenPreview() {
    ClimatScopeTheme {
        ModernHomeScreen(
            onCityClick = {}
        )
    }
}

package com.myapp.climatscope.presentation.screens

import android.Manifest
