package com.myapp.climatscope.data.local

import android.content.ContentValues
import android.content.Context
import android.database.sqlite.SQLiteDatabase
import android.database.sqlite.SQLiteOpenHelper
import android.util.Log
import com.myapp.climatscope.domain.entities.City

private const val DATABASE_NAME = "climatscope.db"
private const val DATABASE_VERSION = 1

private const val CITY_TABLE_NAME = "city"
private const val CITY_KEY_ID = "id"
private const val CITY_KEY_NAME = "name"

private const val CITY_TABLE_CREATE = """
    CREATE TABLE $CITY_TABLE_NAME (
        $CITY_KEY_ID INTEGER PRIMARY KEY,
        $CITY_KEY_NAME TEXT
)    
"""
private const val CITY_QUERY_SELECT_ALL = "SELECT * FROM $CITY_TABLE_NAME"

class CityLocalDataSource(context: Context) :
    SQLiteOpenHelper(context, DATABASE_NAME, null, DATABASE_VERSION) {

    private val TAG = CityLocalDataSource::class.java.simpleName

    override fun onCreate(db: SQLiteDatabase?) {
        db?.execSQL(CITY_TABLE_CREATE)
    }

    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {
        // Handle database upgrade if needed
    }

    suspend fun createCity(city: City): Boolean {
        return try {
            val values = ContentValues().apply {
                put(CITY_KEY_NAME, city.name)
            }

            Log.d(TAG, "creating City: $values")
            val id = writableDatabase.insert(CITY_TABLE_NAME, null, values)
            id > 0
        } catch (e: Exception) {
            Log.e(TAG, "Error creating city", e)
            false
        }
    }

    suspend fun getAllCities(): List<City> {
        val cities = mutableListOf<City>()
        try {
            readableDatabase.rawQuery(CITY_QUERY_SELECT_ALL, null).use { cursor ->
                while (cursor.moveToNext()) {
                    val city = City(
                        id = cursor.getLong(cursor.getColumnIndexOrThrow(CITY_KEY_ID)),
                        name = cursor.getString(cursor.getColumnIndexOrThrow(CITY_KEY_NAME))
                    )
                    cities.add(city)
                }
            }
        } catch (e: Exception) {
            Log.e(TAG, "Error getting cities", e)
        }
        return cities
    }

    suspend fun deleteCity(city: City): Boolean {
        return try {
            Log.d(TAG, "delete city: $city")
            val deleteCount = writableDatabase.delete(
                CITY_TABLE_NAME,
                "$CITY_KEY_ID = ?",
                arrayOf(city.id.toString())
            )
            deleteCount == 1
        } catch (e: Exception) {
            Log.e(TAG, "Error deleting city", e)
            false
        }
    }
}

package com.myapp.climatscope.data.remote

import com.myapp.climatscope.BuildConfig
import retrofit2.Response
import retrofit2.http.GET
import retrofit2.http.Query

interface WeatherApiService {
    @GET("data/2.5/weather?units=metric&lang=fr")
    suspend fun getWeather(
        @Query("q") cityName: String,
        @Query("appid") apiKey: String = BuildConfig.API_KEY,
    ): Response<WeatherResponse>
}

package com.myapp.climatscope.data.remote

import com.myapp.climatscope.domain.entities.Weather
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

class WeatherRemoteDataSource(
    private val apiService: WeatherApiService
) {
    suspend fun getWeatherForCity(cityName: String): Result<Weather> {
        return withContext(Dispatchers.IO) {
            try {
                val response = apiService.getWeather("$cityName,fr")
                if (response.isSuccessful) {
                    response.body()?.let { weatherResponse ->
                        val weather = mapToWeather(weatherResponse)
                        Result.success(weather)
                    } ?: Result.failure(Exception("Empty response body"))
                } else {
                    Result.failure(Exception("API Error: ${response.code()}"))
                }
            } catch (e: Exception) {
                Result.failure(e)
            }
        }
    }

    private fun mapToWeather(response: WeatherResponse): Weather {
        val weatherFirst = response.weather.first()
        return Weather(
            description = weatherFirst.description,
            temperature = response.main.temperature,
            humidity = response.main.humidity,
            pressure = response.main.pressure,
            iconUrl = "https://openweathermap.org/img/wn/${weatherFirst.icon}.png"
        )
    }
}

package com.myapp.climatscope.data.remote

import com.google.gson.annotations.SerializedName

data class WeatherResponse(
    @SerializedName("weather") val weather: List<WeatherInfo>,
    @SerializedName("main") val main: MainInfo
)

data class WeatherInfo(
    @SerializedName("description") val description: String,
    @SerializedName("icon") val icon: String
)

data class MainInfo(
    @SerializedName("temp") val temperature: Double,
    @SerializedName("humidity") val humidity: Int,
    @SerializedName("pressure") val pressure: Int
)

package com.myapp.climatscope.data.repositories

import com.myapp.climatscope.data.local.CityLocalDataSource
import com.myapp.climatscope.domain.entities.City
import com.myapp.climatscope.domain.repositories.CityRepository
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.withContext

class CityRepositoryImpl(
    private val localDataSource: CityLocalDataSource
) : CityRepository {

    override suspend fun getAllCities(): List<City> {
        return withContext(Dispatchers.IO) {
            localDataSource.getAllCities()
        }
    }

    override suspend fun createCity(city: City): Boolean {
        return withContext(Dispatchers.IO) {
            localDataSource.createCity(city)
        }
    }

    override suspend fun deleteCity(city: City): Boolean {
        return withContext(Dispatchers.IO) {
            localDataSource.deleteCity(city)
        }
    }
}

package com.myapp.climatscope.data.repositories

import com.myapp.climatscope.data.remote.WeatherRemoteDataSource
import com.myapp.climatscope.domain.entities.Weather
import com.myapp.climatscope.domain.repositories.WeatherRepository

class WeatherRepositoryImpl(
    private val remoteDataSource: WeatherRemoteDataSource
) : WeatherRepository {

    override suspend fun getWeatherForCity(cityName: String): Result<Weather> {
        return remoteDataSource.getWeatherForCity(cityName)
    }
}

package com.myapp.climatscope.di

import android.content.Context
import com.myapp.climatscope.data.local.CityLocalDataSource
import com.myapp.climatscope.data.remote.WeatherApiService
import com.myapp.climatscope.data.remote.WeatherRemoteDataSource
import com.myapp.climatscope.data.repositories.CityRepositoryImpl
import com.myapp.climatscope.data.repositories.WeatherRepositoryImpl
import com.myapp.climatscope.domain.repositories.CityRepository
import com.myapp.climatscope.domain.repositories.WeatherRepository
import com.myapp.climatscope.domain.usecases.CreateCityUseCase
import com.myapp.climatscope.domain.usecases.DeleteCityUseCase
import com.myapp.climatscope.domain.usecases.GetAllCitiesUseCase
import com.myapp.climatscope.domain.usecases.GetWeatherUseCase
import com.myapp.climatscope.presentation.viewmodels.CityViewModelFactory
import com.myapp.climatscope.presentation.viewmodels.WeatherViewModelFactory
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import retrofit2.Retrofit
import retrofit2.converter.gson.GsonConverterFactory

class DependencyContainer(private val context: Context) {

    // Network
    private val httpClient = OkHttpClient.Builder()
        .addInterceptor(HttpLoggingInterceptor().setLevel(HttpLoggingInterceptor.Level.BODY))
        .build()

    private val retrofit = Retrofit.Builder()
        .client(httpClient)
        .baseUrl("https://api.openweathermap.org/")
        .addConverterFactory(GsonConverterFactory.create())
        .build()

    // Data Sources
    private val weatherApiService: WeatherApiService = retrofit.create(WeatherApiService::class.java)
    private val weatherRemoteDataSource = WeatherRemoteDataSource(weatherApiService)
    private val cityLocalDataSource = CityLocalDataSource(context)

    // Repositories
    private val cityRepository: CityRepository = CityRepositoryImpl(cityLocalDataSource)
    private val weatherRepository: WeatherRepository = WeatherRepositoryImpl(weatherRemoteDataSource)

    // Use Cases
    private val getAllCitiesUseCase = GetAllCitiesUseCase(cityRepository)
    private val createCityUseCase = CreateCityUseCase(cityRepository)
    private val deleteCityUseCase = DeleteCityUseCase(cityRepository)
    private val getWeatherUseCase = GetWeatherUseCase(weatherRepository)

    // ViewModels Factories
    val cityViewModelFactory = CityViewModelFactory(
        getAllCitiesUseCase,
        createCityUseCase,
        deleteCityUseCase
    )

    val weatherViewModelFactory = WeatherViewModelFactory(getWeatherUseCase)
}

