package com.myapp.climatscope.presentation.navigation

import androidx.compose.runtime.Composable
import androidx.lifecycle.viewmodel.compose.viewModel
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import androidx.navigation.compose.rememberNavController
import com.myapp.climatscope.App
import com.myapp.climatscope.presentation.screens.CityScreen
import com.myapp.climatscope.presentation.screens.WeatherScreen
import com.myapp.climatscope.presentation.viewmodels.CityViewModel
import com.myapp.climatscope.presentation.viewmodels.WeatherViewModel

sealed class Screen(val route: String) {
    object Cities : Screen("cities")
    object Weather : Screen("weather/{cityName}") {
        fun createRoute(cityName: String) = "weather/$cityName"
    }
}

@Composable
fun ClimatScopeNavigation(
    navController: NavHostController = rememberNavController(),
    app: App
) {
    NavHost(
        navController = navController,
        startDestination = Screen.Cities.route
    ) {
        composable(Screen.Cities.route) {
            val cityViewModel: CityViewModel = viewModel(
                factory = app.dependencyContainer.cityViewModelFactory
            )

            CityScreen(
                viewModel = cityViewModel,
                onCityClick = { city ->
                    navController.navigate(Screen.Weather.createRoute(city.name))
                }
            )
        }

        composable(Screen.Weather.route) { backStackEntry ->
            val cityName = backStackEntry.arguments?.getString("cityName") ?: ""
            val weatherViewModel: WeatherViewModel = viewModel(
                factory = app.dependencyContainer.weatherViewModelFactory
            )

            WeatherScreen(
                cityName = cityName,
                viewModel = weatherViewModel,
                onBackClick = {
                    navController.popBackStack()
                }
            )
        }
    }
}

package com.myapp.climatscope.presentation.screens

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Add
import androidx.compose.material.icons.filled.Delete
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.myapp.climatscope.domain.entities.City
import com.myapp.climatscope.presentation.viewmodels.CityViewModel

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun CityScreen(
    onCityClick: (City) -> Unit,
    viewModel: CityViewModel = viewModel()
) {
    val uiState by viewModel.uiState.collectAsState()
    var showAddDialog by remember { mutableStateOf(false) }

    Scaffold(
        topBar = {
            TopAppBar(
                title = { Text("Mes Villes") },
                actions = {
                    IconButton(onClick = { showAddDialog = true }) {
                        Icon(Icons.Default.Add, contentDescription = "Ajouter une ville")
                    }
                }
            )
        }
    ) { paddingValues ->
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(paddingValues)
        ) {
            when {
                uiState.isLoading -> {
                    Box(
                        modifier = Modifier.fillMaxSize(),
                        contentAlignment = Alignment.Center
                    ) {
                        CircularProgressIndicator()
                    }
                }
                uiState.cities.isEmpty() -> {
                    EmptyStateMessage()
                }
                else -> {
                    CityList(
                        cities = uiState.cities,
                        onCityClick = onCityClick,
                        onDeleteCity = { city -> viewModel.deleteCity(city) }
                    )
                }
            }
        }

        // Gestion des erreurs
        uiState.errorMessage?.let { message ->
            LaunchedEffect(message) {
                // Afficher le message d'erreur
                viewModel.clearError()
            }
        }
    }

    // Dialog d'ajout de ville
    if (showAddDialog) {
        AddCityDialog(
            onDismiss = { showAddDialog = false },
            onAddCity = { cityName ->
                viewModel.createCity(cityName)
                showAddDialog = false
            }
        )
    }
}

@Composable
private fun EmptyStateMessage() {
    Box(
        modifier = Modifier.fillMaxSize(),
        contentAlignment = Alignment.Center
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.Center
        ) {
            Text(
                text = "Aucune ville ajoute",
                style = MaterialTheme.typography.titleMedium,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )
            Spacer(modifier = Modifier.height(8.dp))
            Text(
                text = "Appuyez sur + pour ajouter votre premire ville",
                style = MaterialTheme.typography.bodyMedium,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )
        }
    }
}

@Composable
private fun CityList(
    cities: List<City>,
    onCityClick: (City) -> Unit,
    onDeleteCity: (City) -> Unit
) {
    LazyColumn(
        modifier = Modifier.fillMaxSize(),
        contentPadding = PaddingValues(16.dp),
        verticalArrangement = Arrangement.spacedBy(8.dp)
    ) {
        items(cities) { city ->
            CityItem(
                city = city,
                onClick = { onCityClick(city) },
                onDelete = { onDeleteCity(city) }
            )
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun CityItem(
    city: City,
    onClick: () -> Unit,
    onDelete: () -> Unit
) {
    Card(
        modifier = Modifier.fillMaxWidth(),
        onClick = onClick
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(16.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = city.name,
                style = MaterialTheme.typography.bodyLarge,
                fontWeight = FontWeight.Medium
            )

            IconButton(onClick = onDelete) {
                Icon(
                    Icons.Default.Delete,
                    contentDescription = "Supprimer ${city.name}",
                    tint = MaterialTheme.colorScheme.error
                )
            }
        }
    }
}

@Composable
private fun AddCityDialog(
    onDismiss: () -> Unit,
    onAddCity: (String) -> Unit
) {
    var cityName by remember { mutableStateOf("") }

    AlertDialog(
        onDismissRequest = onDismiss,
        title = { Text("Ajouter une ville") },
        text = {
            OutlinedTextField(
                value = cityName,
                onValueChange = { cityName = it },
                label = { Text("Nom de la ville") },
                singleLine = true
            )
        },
        confirmButton = {
            TextButton(
                onClick = {
                    if (cityName.isNotBlank()) {
                        onAddCity(cityName.trim())
                    }
                }
            ) {
                Text("Ajouter")
            }
        },
        dismissButton = {
            TextButton(onClick = onDismiss) {
                Text("Annuler")
            }
        }
    )
}

