@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun EnhancedHomeScreen(
    onCityClick: (City) -> Unit,
    cityViewModel: CityViewModel = viewModel(),
    weatherViewModel: WeatherViewModel = viewModel()
) {
    val cityUiState by cityViewModel.uiState.collectAsState()
    val weatherUiState by weatherViewModel.uiState.collectAsState()
    var showBottomSheet by remember { mutableStateOf(false) }
    val scope = rememberCoroutineScope()

    // Gestionnaire des permissions de golocalisation
    val locationPermissionLauncher = rememberLauncherForActivityResult(
        contract = ActivityResultContracts.RequestMultiplePermissions()
    ) { permissions ->
        val fineLocationGranted = permissions[Manifest.permission.ACCESS_FINE_LOCATION] ?: false
        val coarseLocationGranted = permissions[Manifest.permission.ACCESS_COARSE_LOCATION] ?: false

        if (fineLocationGranted || coarseLocationGranted) {
            scope.launch {
                // Logique de golocalisation sera implmente ici
                weatherViewModel.loadWeatherForCity("Ma position")
            }
        }
    }

    // Auto-chargement de la premire ville
    LaunchedEffect(Unit) {
        if (cityUiState.cities.isNotEmpty() && weatherUiState.weather == null) {
            weatherViewModel.loadWeatherForCity(cityUiState.cities.first().name)
        }
    }

    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(
                brush = Brush.verticalGradient(
                    colors = listOf(
                        MaterialTheme.colorScheme.primary.copy(alpha = 0.05f),
                        MaterialTheme.colorScheme.background,
                        MaterialTheme.colorScheme.surface.copy(alpha = 0.5f)
                    )
                )
            )
    ) {
        LazyColumn(
            modifier = Modifier
                .fillMaxSize()
                .statusBarsPadding(),
            contentPadding = PaddingValues(bottom = 100.dp),
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            // En-tte moderne avec localisation
            item {
                ModernLocationHeader(
                    cityName = weatherUiState.cityName.ifEmpty { "Choisir une ville" },
                    onLocationClick = {
                        locationPermissionLauncher.launch(
                            arrayOf(
                                Manifest.permission.ACCESS_FINE_LOCATION,
                                Manifest.permission.ACCESS_COARSE_LOCATION
                            )
                        )
                    },
                    onMenuClick = { showBottomSheet = true },
                    modifier = Modifier.padding(horizontal = 16.dp)
                )
            }

            // Section mto principale
            item {
                weatherUiState.weather?.let { weather ->
                    EnhancedWeatherSection(
                        weather = weather,
                        isLoading = weatherUiState.isLoading,
                        modifier = Modifier.padding(horizontal = 16.dp)
                    )
                } ?: run {
                    if (weatherUiState.isLoading) {
                        LoadingWeatherCard(
                            modifier = Modifier.padding(horizontal = 16.dp)
                        )
                    } else {
                        WelcomeCard(
                            onLocationClick = {
                                locationPermissionLauncher.launch(
                                    arrayOf(
                                        Manifest.permission.ACCESS_FINE_LOCATION,
                                        Manifest.permission.ACCESS_COARSE_LOCATION
                                    )
                                )
                            },
                            onSelectCityClick = { showBottomSheet = true },
                            modifier = Modifier.padding(horizontal = 16.dp)
                        )
                    }
                }
            }

            // Section des villes favorites (aperu horizontal)
            if (cityUiState.cities.isNotEmpty()) {
                item {
                    FavoriteCitiesSection(
                        cities = cityUiState.cities,
                        onCityClick = { city ->
                            weatherViewModel.loadWeatherForCity(city.name)
                            onCityClick(city)
                        },
                        onSeeAllClick = { showBottomSheet = true }
                    )
                }
            }

            // Conseils mto
            weatherUiState.weather?.let { weather ->
                item {
                    WeatherAdviceCard(
                        weather = weather,
                        modifier = Modifier.padding(horizontal = 16.dp)
                    )
                }
            }
        }

        // Bottom Sheet pour la gestion des villes
        if (showBottomSheet) {
            EnhancedCityBottomSheet(
                cities = cityUiState.cities,
                isLoading = cityUiState.isLoading,
                onCitySelected = { city ->
                    weatherViewModel.loadWeatherForCity(city.name)
                    showBottomSheet = false
                },
                onAddCity = { cityName ->
                    cityViewModel.createCity(cityName)
                },
                onDeleteCity = { city ->
                    cityViewModel.deleteCity(city)
                },
                onDismiss = { showBottomSheet = false }
            )
        }
    }

    // Gestion des erreurs avec SnackBar
    weatherUiState.errorMessage?.let { message ->
        LaunchedEffect(message) {
            // Afficher un SnackBar d'erreur
            weatherViewModel.clearError()
        }
    }
}

@Composable
private fun EnhancedWeatherSection(
    weather: Weather,
    isLoading: Boolean,
    modifier: Modifier = Modifier
) {
    Column(
        modifier = modifier,
        verticalArrangement = Arrangement.spacedBy(16.dp)
    ) {
        // Carte mto principale
        ModernWeatherCard(
            temperature = "${weather.temperature.toInt()}",
            description = weather.description.replaceFirstChar { it.uppercase() },
            iconUrl = weather.iconUrl
        )

        // Mtriques mto en grille
        LazyRow(
            horizontalArrangement = Arrangement.spacedBy(12.dp),
            contentPadding = PaddingValues(horizontal = 4.dp)
        ) {
            item {
                WeatherMetricCard(
                    icon = Icons.Default.Star,
                    label = "Humidit",
                    value = "${weather.humidity}%",
                    modifier = Modifier.width(120.dp)
                )
            }
            item {
                WeatherMetricCard(
                    icon = Icons.Default.Favorite,
                    label = "Ressenti",
                    value = "${weather.feelsLike.toInt()}",
                    modifier = Modifier.width(120.dp)
                )
            }
            item {
                WeatherMetricCard(
                    icon = Icons.Default.Info,
                    label = "Pression",
                    value = "${weather.pressure} hPa",
                    modifier = Modifier.width(120.dp)
                )
            }
            item {
                WeatherMetricCard(
                    icon = Icons.Default.Send,
                    label = "Vent",
                    value = "${weather.windSpeed} km/h",
                    modifier = Modifier.width(120.dp)
                )
            }
        }
    }
}

@Composable
private fun WelcomeCard(
    onLocationClick: () -> Unit,
    onSelectCityClick: () -> Unit,
    modifier: Modifier = Modifier
) {
    Card(
        modifier = modifier.fillMaxWidth(),
        shape = RoundedCornerShape(24.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.primaryContainer.copy(alpha = 0.3f)
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 8.dp)
    ) {
        Column(
            modifier = Modifier
                .fillMaxWidth()
                .padding(32.dp),
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.spacedBy(16.dp)
        ) {
            Icon(
                imageVector = Icons.Default.Place,
                contentDescription = null,
                modifier = Modifier.size(64.dp),
                tint = MaterialTheme.colorScheme.primary
            )

            Text(
                text = "Bienvenue dans ClimatScope",
                style = MaterialTheme.typography.headlineSmall,
                fontWeight = FontWeight.Bold,
                textAlign = TextAlign.Center,
                color = MaterialTheme.colorScheme.onSurface
            )

            Text(
                text = "Dcouvrez la mto en temps rel avec une interface moderne et intuitive",
                style = MaterialTheme.typography.bodyLarge,
                textAlign = TextAlign.Center,
                color = MaterialTheme.colorScheme.onSurfaceVariant
            )

            Column(
                verticalArrangement = Arrangement.spacedBy(12.dp)
            ) {
                Button(
                    onClick = onLocationClick,
                    modifier = Modifier.fillMaxWidth(),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = MaterialTheme.colorScheme.primary
                    )
                ) {
                    Icon(Icons.Default.LocationOn, contentDescription = null)
                    Spacer(modifier = Modifier.width(8.dp))
                    Text("Utiliser ma position")
                }

                OutlinedButton(
                    onClick = onSelectCityClick,
                    modifier = Modifier.fillMaxWidth()
                ) {
                    Icon(Icons.Default.Search, contentDescription = null)
                    Spacer(modifier = Modifier.width(8.dp))
                    Text("Choisir une ville")
                }
            }
        }
    }
}

@Composable
private fun FavoriteCitiesSection(
    cities: List<City>,
    onCityClick: (City) -> Unit,
    onSeeAllClick: () -> Unit
) {
    Column {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 16.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Mes villes",
                style = MaterialTheme.typography.titleLarge,
                fontWeight = FontWeight.Bold
            )

            TextButton(onClick = onSeeAllClick) {
                Text("Voir tout")
                Icon(
                    Icons.Default.ArrowForward,
                    contentDescription = null,
                    modifier = Modifier.size(16.dp)
                )
            }
        }

        LazyRow(
            contentPadding = PaddingValues(horizontal = 16.dp),
            horizontalArrangement = Arrangement.spacedBy(12.dp)
        ) {
            items(cities.take(5)) { city ->
                CityQuickCard(
                    city = city,
                    onClick = { onCityClick(city) }
                )
            }
        }
    }
}

@OptIn(ExperimentalMaterial3Api::class)
@Composable
private fun CityQuickCard(
    city: City,
    onClick: () -> Unit
) {
    Card(
        onClick = onClick,
        modifier = Modifier.width(120.dp),
        shape = RoundedCornerShape(16.dp),
        colors = CardDefaults.cardColors(
            containerColor = MaterialTheme.colorScheme.surface
        ),
        elevation = CardDefaults.cardElevation(defaultElevation = 4.dp)
    ) {
        Column(
            modifier = Modifier.padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Text(
                text = city.name.first().uppercase(),
                style = MaterialTheme.typography.headlineMedium,
                fontWeight = FontWeight.Bold,
                color = MaterialTheme.colorScheme.primary
            )
            Text(
                text = city.name,
                style = MaterialTheme.typography.bodyMedium,
                textAlign = TextAlign.Center
            )
        }
    }
}

